    function s7upf_add_advantage($settings, $value)
    {
        $val = $value;
        $html = '<div class="wrap-param">';
        $html .=    '<div class="param-list">';
        
        parse_str(urldecode($value), $advantage);
        if(is_array($advantage)) 
        {
            $i = 1;
            foreach ($advantage as $key => $value) {
                if(!isset($value['url'])) $value['url'] = '';
                $html .=    '<div class="param-item" data-item="'.$i.'">';
                 $html .=       '<strong>'.esc_html__("Image","micar").' '.$i.':</strong></br>';
                    $html .=    '<div class="wpb_el_type_attach_image edit_form_line">
                                    <input type="hidden" class="param-field wpb_vc_param_value gallery_widget_attached_images_ids images attach_images" name="'.$i.'[image]" value="'.$value['image'].'">
                                    <div class="gallery_widget_attached_images">
                                        <ul class="gallery_widget_attached_images_list ui-sortable">';
                    if(!empty($value['image'])){
                        $img = wp_get_attachment_image_src( $value['image'] );
                        $html .=            '<li class="added ui-sortable-handle">
                                                <img rel="'.$value['image'].'" src="'.esc_url($img[0]).'">
                                                <a href="#" class="icon-remove"></a>
                                            </li>';
                    }
                    $html .=            '</ul>
                                    </div>
                                    <div class="gallery_widget_site_images"></div>
                                    <a class="gallery_widget_add_images" href="#" use-single="true" title="'.esc_attr__("Add images","micar").'">'.esc_html__("Add images","micar").'</a>
                                    <span class="vc_description vc_clearfix">'.esc_html__("Select images from media library.","micar").'</span>
                                </div>';
                    $html .=    '<label>'.esc_html__("Title","micar").' </label><input style="width:65%;margin-right:10px;margin-bottom:15px" class="param-field" name="'.$i.'[title]" value="'.$value['title'].'" type="text" ></br>';
                    $html .=    '<label>'.esc_html__("Description 1","micar").' </label><input style="width:65%;margin-right:10px;margin-bottom:15px" class="param-field" name="'.$i.'[des1]" value="'.$value['des1'].'" type="text" ></br>';
                    $html .=    '<label>'.esc_html__("Description 2","micar").' </label><input style="width:65%;margin-right:10px;margin-bottom:15px" class="param-field" name="'.$i.'[des2]" value="'.$value['des2'].'" type="text" ></br>';
