        $html .=                '<div class="wpb_el_type_attach_image edit_form_line"><input type="hidden" class="param-field wpb_vc_param_value gallery_widget_attached_images_ids images attach_images" name="#key[image]" value=""><div class="gallery_widget_attached_images"><ul class="gallery_widget_attached_images_list ui-sortable"></ul></div><div class="gallery_widget_site_images"></div><a class="gallery_widget_add_images" href="#" use-single="true" title="'.esc_attr__("Add images","micar").'">'.esc_html__("Add images","micar").'</a><span class="vc_description vc_clearfix">'.esc_html__("Select images from media library.","micar").'</span></div>';            
